/*Table structure for table `proposal` */

DROP TABLE IF EXISTS `proposal`;

CREATE TABLE `proposal` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '投诉编号',
  `uId` int(11) NOT NULL COMMENT '建议人',
  `type` int(11) NOT NULL COMMENT '投诉类型0建议1投诉',
  `title` varchar(50) NOT NULL COMMENT '标题',
  `content` varchar(255) NOT NULL COMMENT '建议内容',
  `upTime` date NOT NULL COMMENT '提交时间',
  `cUid` int(11) DEFAULT NULL COMMENT '处理人',
  `prate` int(11) NOT NULL COMMENT '处理状态0以处理1未处理',
  `ptime` date DEFAULT NULL COMMENT '回复时间',
  PRIMARY KEY (`id`),
  KEY `fk_proposal_user` (`uId`),
  KEY `fk_proposal_cuser` (`cUid`),
  CONSTRAINT `fk_proposal_cuser` FOREIGN KEY (`cUid`) REFERENCES `gamecarduser` (`id`),
  CONSTRAINT `fk_proposal_user` FOREIGN KEY (`uId`) REFERENCES `gamecarduser` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='建议表';

/*Data for the table `proposal` */

/*Table structure for table `recharge` */

DROP TABLE IF EXISTS `recharge`;

CREATE TABLE `recharge` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '充值编号',
  `uId` int(11) NOT NULL COMMENT '用户编号',
  `money` double(10,2) NOT NULL COMMENT '充值金额',
  `paymenttype` int(11) NOT NULL COMMENT '充值类型',
  `rate` int(11) NOT NULL COMMENT '充值状态0成功1失败',
  PRIMARY KEY (`id`),
  KEY `fk_Recharge_user` (`uId`),
  KEY `fk_Recharge_payment` (`paymenttype`),
  CONSTRAINT `fk_Recharge_payment` FOREIGN KEY (`paymenttype`) REFERENCES `paymenttype` (`paymenttype`),
  CONSTRAINT `fk_Recharge_user` FOREIGN KEY (`uId`) REFERENCES `gamecarduser` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='充值表';

/*Data for the table `recharge` */

/*Table structure for table `shoppingcart` */

DROP TABLE IF EXISTS `shoppingcart`;

CREATE TABLE `shoppingcart` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '购物车编号',
  `uId` int(11) NOT NULL COMMENT '用户编号',
  `cId` int(11) NOT NULL COMMENT '点卡编号',
  `gid` int(11) NOT NULL COMMENT '游戏编号',
  `num` int(11) NOT NULL COMMENT '购买数量',
  `totalPrice` double(10,2) NOT NULL COMMENT '金额统计',
  PRIMARY KEY (`id`),
  KEY `fk_ShoppingCart_user` (`uId`),
  KEY `fk_ShoppingCart_card` (`cId`),
  KEY `fk_ShoppingCart_game` (`gid`),
  CONSTRAINT `fk_ShoppingCart_card` FOREIGN KEY (`cId`) REFERENCES `cards` (`cId`),
  CONSTRAINT `fk_ShoppingCart_game` FOREIGN KEY (`gid`) REFERENCES `games` (`gid`),
  CONSTRAINT `fk_ShoppingCart_user` FOREIGN KEY (`uId`) REFERENCES `gamecarduser` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='购物车表';

